function aspectRatio(selector,ratio){var tag=document.querySelectorAll(selector);var style="";var count=0;for(var i=0;i<tag.length;i++){var attr=selector.replace(/\W+/g,"");tag[i].setAttribute("data-aspect-ratio-"+attr,count);style+="\n/* "+selector+" { aspect-ratio: "+ratio+"; } */\n"+"[data-aspect-ratio-"+attr+'="'+count+'"] {\n'+"  height: "+tag[i].offsetWidth/ratio+"px;\n"+"}\n";count++}return style}
