function closest(selector,ancestor,rule){var tag=document.querySelectorAll(selector);var style="";var count=0;for(var i=0;i<tag.length;i++){var attr=(selector+ancestor).replace(/\W+/g,"");tag[i].closest(ancestor).setAttribute("data-closest-"+attr,count);style+="\n/* "+selector+":closest("+ancestor+") */\n"+"[data-closest-"+attr+'="'+count+'"] {\n'+"  "+rule+"\n"+"}\n";count++}return style}
