function container(selector,test,childSelector,rule){var tag=document.querySelectorAll(selector);var style="";var count=0;for(var i=0;i<tag.length;i++){var attr=(selector+test).replace(/\W+/g,"");var func=new Function("return "+test);if(func.call(tag[i])){tag[i].setAttribute("data-container-"+attr,count);var container="[data-container-"+attr+'="'+count+'"]';style+="\n/* "+selector+"("+test+") "+childSelector+" */\n"+container+" "+childSelector+" {\n"+"  "+rule+"\n"+"}\n";count++}else{tag[i].setAttribute("data-container-"+attr,"")}}return style}
