function parent(selector,rule){var tag=document.querySelectorAll(selector);var style="";var count=0;for(var i=0;i<tag.length;i++){var attr=selector.replace(/\W+/g,"");tag[i].parentNode.setAttribute("data-parent-"+attr,count);style+="\n/* "+selector+":parent */\n"+"[data-parent-"+attr+'="'+count+'"] {\n'+"  "+rule+"\n"+"}\n";count++}return style}
