function prev(selector,rule){var tag=document.querySelectorAll(selector);var style="";var count=0;for(var i=0;i<tag.length;i++){var attr=selector.replace(/\W+/g,"");tag[i].previousElementSibling.setAttribute("data-prev-"+attr,count);style+="\n/* "+selector+":prev */\n"+"[data-prev-"+attr+'="'+count+'"] {\n'+"  "+rule+"\n"+"}\n";count++}return style}
